export type Interaction = {
  id: string
  // type of the interaction
  type: 'chat' | 'navigate'
  // user content of the interaction
  // for chat, it's the user message
  // for navigate, it's the url
  userContent: string | null
  // id of the context interaction
  contextId: string | null
  // id of the next chat interaction
  nextChatId: string | null
  // created at timestamp
  createdAt: number
}

// For chat interactions, this represents the content generated by an AI assistant
// This is a mutable object
export type AssistantContent = {
  // The same as the interaction id
  id: string
  // model used to generate the content
  model: string
  // content generated by the assistant
  content: string
  // updated at timestamp
  updatedAt: number
}

// For navigate interactions, this represents additional information about the page
// This is a mutable object
export type PageInfo = {
  // The same as the interaction id
  id: string
  // title of the page
  title: string
  // description of the page
  description: string | null
  // favicon url of the page
  favIconUrl: string | null
  // image url of the page
  imageUrl: string | null
  // updated at timestamp
  updatedAt: number
}

